const route = await fetch('api/route-details/bus-1.json').then(r => r.json());
const now = new Date();
const currentTime = now.getHours() * 60 + now.getMinutes();
const times = route.schedules.weekday.forward.times
  .map(t => t.split(':').map(Number))
  .map(([h, m]) => h * 60 + m)
  .filter(t => t > currentTime);
const nextTime = times[0];
console.log(`Следующий рейс через ${nextTime - currentTime} минут`);
